DECLARE CURSQL CURSOR FOR
SELECT ID,COMMAND
FROM TMPCMD
ORDER BY ID

OPEN CURSQL;

FETCH NEXT FROM CURSQL INTO @ID,@STRSQL;
WHILE @@FETCH_STATUS=0
BEGIN 
	SET @STRERROR=NULL;
	SET @STARTTIME=GETDATE();
	BEGIN TRY
		EXECUTE (@STRSQL);
    END TRY
    BEGIN CATCH
		 SET @STRERROR = ERROR_NUMBER()
    END CATCH;
    SET @ENDTIME=GETDATE();
		UPDATE TMPCMD 
			SET TIME_EXEC=@ENDTIME-@STARTTIME, MSGERRO=@STRERROR
			WHERE TMPCMD.ID=@ID

    FETCH NEXT FROM CURSQL INTO @ID,@STRSQL;

    --WAITFOR DELAY '00:00:30';
END;

CLOSE CURSQL;
DEALLOCATE CURSQL;
